mapper.select:
select * from member where mem_type='TRAINER' 

	and gym_code=#{gym_code}

order by mem_regidate desc

mapper.update:
update member set mem_pass=#{mem_pass}, mem_name=#{mem_name}, mem_gender=#{mem_gender},
	mem_phone=#{mem_phone}, mem_address=#{mem_address}, mem_status=#{mem_status}, gym_code=#{gym_code},
	mem_career=#{mem_career}, mem_comment=#{mem_comment}
where mem_id=#{mem_id}
