mapper.select:
SELECT l.*, mem_name "trainer_name" FROM lesson l inner join member m on m.mem_id=l.trainer_id where lesson_idx NOT IN (SELECT lesson_idx FROM reservation WHERE user_id = #{arg0}) and lesson_day >= sysdate-1 and (
(trainer_id = #{list.trainer_id} AND lesson_category = #{list.product_category})
AND lesson_day <= #{list.membership_end}
)

select mem_name from reservation r 
inner join member m on m.mem_id = r.user_id
where lesson_idx=#{lesson_idx}
